const add = {
    id: "Add",
    inputs: {"a": {"mode": "required"}, "b": {"mode": "required"}},
    outputs: {"r": {}},
    fn: (inputs, outputs) => {
        outputs.r.next(inputs.a + inputs.b);
    }
}

const sub = {
    id: "Sub",
    inputs: {"a": {"mode": "required"}, "b": {"mode": "required"}},
    outputs: {"r": {}},
    fn: (inputs, outputs) => {
        outputs.r.next(inputs.a - inputs.b);
    }
}
module.exports = {
    add,
    sub
}